int next[2][2] = {
    {-1, -1},
    {-1, -1}
}; // Array of possible next [x,y] positions


void updatePositions(){
    sqrs_pos[id][0] = x;
    sqrs_pos[id][1] = y;
}


void initializePositions(){
    sqrs_pos[id][0] = 0;
    sqrs_pos[id][1] = 0;
}



// Returns possible next positions from (x,y)
void getNextPositions(int x, int y, int prev_x, int prev_y) {

    next[0][0] = -1;
    next[0][1] = -1;
    next[1][0] = -1;
    next[1][1] = -1;
    bifurcation = false;

    // Main Tower reached - no further movement
    if(x == MAIN_X && y == MAIN_Y) {
        return;
    }

    // Bifurcation point 1
    if(x == BIFURCATION1_X && y == BIFURCATION1_Y) {
        // Move up
        next[0][0] = x;
        next[0][1] = y+1;
         // Move down
        next[1][0] = x;
        next[1][1] = y-1;
        bifurcation = true;
        return;
    }

    // Bifurcation point 2
    if(x == BIFURCATION2_X && y == BIFURCATION2_Y) {
        // Move down
        next[0][0] = x;
        next[0][1] = y-1;
        // Move right
        next[1][0] = x+1;
        next[1][1] = y;
        bifurcation = true;
        return;
    }

    // Horizontal movement
    if (x+1 != prev_x || y != prev_y){
        if (x < WIDTH - 1 && map[y][x + 1] == PATH) {
            next[0][0] = x + 1;
            next[0][1] = y;
            return;
        }
    }
    // Vertical movement
    if (y < HEIGHT - 1 && map[y + 1][x] == PATH && !(x == prev_x && y+1 == prev_y)) {
        next[0][0] = x;
        next[0][1] = y + 1;
        return;
    }
    if (y > 0 && map[y - 1][x] == PATH && !(x == prev_x && y-1 == prev_y)) {
        next[0][0] = x;
        next[0][1] = y - 1;
        return;
    }
    // Edge case handling
    return ;
}

