        // Move up
        next[0][0] = x;
        next[0][1] = y+1;
         // Move down
        next[1][0] = x;
        next[1][1] = y-1;
        return;
    }

    // Bifurcation point 2
    if(x == BIFURCATION2_X && y == BIFURCATION2_Y) {
        // Move down
        next[0][0] = x;
        next[0][1] = y-1;
        // Move right
        next[1][0] = x+1;
        next[1][1] = y;
        return;
    }

    // Default forward movement
    if (x+1 != prev_x || y != prev_y){
        if (x < WIDTH - 1 && map[y][x + 1] == PATH) {
            next[0][0] = x + 1;
            next[0][1] = y;
            return;
        }
    }
    // Vertical movement
    if (y < HEIGHT - 1 && map[y + 1][x] == PATH && !(x == prev_x && y+1 == prev_y)) {
        next[0][0] = x;
        next[0][1] = y + 1;
        return;
    }
    if (y > 0 && map[y - 1][x] == PATH && !(x == prev_x && y-1 == prev_y)) {
        next[0][0] = x;
        next[0][1] = y - 1;
        return;
    }
    // Edge case handling
    return ;
}

